_login () {
#loop conf
	Lnl=$(cat $TMP/login.txt | wc -l)
	nl=1
	>$TMP/usernames.txt
	for num in `seq $Lnl -1 $nl`;
	do
		login=$(cat $TMP/login.txt | tail -n $Lnl | head -n 1)
		[[ -n $login ]] && {
			num=$[$num+1]
#username
		archive="$(echo "$login" | awk '{print tolower($0)}' | sed 's/\ /_/g;s/\;/./g' | awk -F"[.]" '{ print $1 }')"
		echo $archive >>$TMP/usernames.txt
#password
		password="$(echo "$login" | sed 's/\ /_/g;s/\;/./g' | awk -F"[.]" '{ print $2 }')"
		mkdir -p $TMP/$archive
#cmd log
		echo 'key Down Arrow' >$TMP/$archive/$archive
		echo 'key Down Arrow' >>$TMP/$archive/$archive
		echo 'key Down Arrow' >>$TMP/$archive/$archive
		for i in `echo $archive|fold -w1`
		do
			echo 'key '$i >> $TMP/$archive/$archive
		done
		echo 'key ^J' >> $TMP/$archive/$archive
		for i in `echo $password|fold -w1`
		do
			echo 'key '$i >> $TMP/$archive/$archive
		done
		echo 'key ^J' >> $TMP/$archive/$archive
		echo 'key ^J' >> $TMP/$archive/$archive
		echo 'key q' >> $TMP/$archive/$archive
		echo 'key y' >> $TMP/$archive/$archive
		sed -i 's/_/\<space\>/g' $TMP/$archive/$archive
#cookies
		>$TMP/$archive/.cookie
		echo "U0VUX0NPT0tJRVM6VFJVRQpBQ0NFUFRfQUxMX0NPT0tJRVM6VFJVRQpQRVJTSVNURU5UX0NPT0tJRVM6VFJVRQo=" | base64 -d >$TMP/$archive/cfg
		echo "COOKIE_FILE:$TMP/$archive/.cookie" >>$TMP/$archive/cfg
#UA base64 list
		echo "" | base64 -d | head -n $num | tail -n 1 >$TMP/$archive/.ua
#run logins
		( torsocks lynx -image_links -cfg=$TMP/$archive/cfg -cmd_script=$TMP/$archive/$archive "http://furiadetitas.net/?sign_in=1" -useragent="$(shuf -n1 $TMP/$archive/.ua)" ) &
		_timexit 20
#change tor IP
		pkill -sighup tor
#loop conf
		Lnl=$[$Lnl-1]
	}
	pkill -9 lynx
	reset; clear
	echo "Login: $Lnl"
	sleep 1s
	done
}
